import './App.css';
import Entry from './Entry';
import { useState } from 'react';



function App() {
//   const list =[{
//     id: 1,
//     name: "nagaraj",
//     fees: 2000,
//     dob: 21/1/2001,
//   },
//   {
//     id: 2,
//     name: "satvik",
//     fees: 3000,
//     dob: 13/32014,
//   },

//   {
//     id: 3,
//     name: "sanvi",
//     fees: 1000,
//     dob: 3/5/2015,
//   }
// ]
  const [isNewButtonClicked,setIsNewButtonClicked] = useState(true);
  const [studentList, setStudentList]= useState([]);

  // useEffect(()=>{
  //   alert("Hi");
  // },[studentList,isNewButtonClicked]);


 function callBackFromEntry(obj){

 setStudentList([...studentList,obj]);
setIsNewButtonClicked(!isNewButtonClicked);
 }

function handleOnSubmit(event){
  event.preventDefault();
  setIsNewButtonClicked(!isNewButtonClicked);
}


  return (

    <div className='main-table'>      
      <div>
        <form id="inp" onClick={(e)=>handleOnSubmit(e)}>
        <input type='text'className='input'/>
        <button type="submit" >ADD</button>
        </form>
     </div>
      <table>
        <thead>
        <tr>
        <th>NAME</th>
        <th>FEES</th>
        </tr>
        </thead>
    {studentList.map((obj)=> {
         return(
           <tr key={obj.id}> 
            <td>
            {obj.name}
            </td>
            <td>
              {obj.fees}
            </td>

           </tr> 
         );
    })
  }

</table>
<p>{!isNewButtonClicked?<Entry
 callback={callBackFromEntry}
/>:""}</p>
</div>
  );
}

export default App;
