  const [isNewButtonClicked,setIsNewButtonClicked] = useState(false);
  const list =[{
    id: 1,
    name: "nagaraj",
    fees: 2000,
    dob: 21/1/2001,
  },
  {
    id: 2,
    name: "satvik",
    fees: 3000,
    dob: 13/32014,
  },

  {
    id: 3,
    name: "sanvi",
    fees: 1000,
    dob: 3/5/2015,
  }
]

function handleOnSubmit(){

  setIsNewButtonClicked(true);
}
  return (
    <div className='main-table'>
      <div>
      <div>
        <form id="inp" onClick={handleOnSubmit()}>
        <input type='text'className='input'/>
        <button type="submit" >ADD</button>
        </form>
     </div>
      <table>
        <thead>
        <tr>
        <th>NAME</th>
        <th>FEES</th>
        </tr>
        </thead>
    {list.map((obj)=> {
         return(
           <tr key={obj.id}> 
            <td>
            {obj.name}
            </td>
            <td>
              {obj.fees}
            </td>

           </tr> 
         );
    })
  }

</table>
{isNewButtonClicked?<Entry></Entry>:""}
</div>

    </div>
  );
}

export default App;
